import React, { Component } from 'react'

class SortBy extends Component {
    constructor(props) {
        super(props)
        this.state = {
            display: props.sortDisplay
        }
    }
    change= e =>{
        console.log(e.target.value);

        this.props.sortSelect(e.value);
    }
    render() {
        let str= "";
        let choice= this.state.display+"";
        switch(choice){
            case "2" : str= "Project Name Reverse";
                break;
            case "3" : str= "Developer Name";
                break;
            case "4" : str= "Developer Name Reverse";
                break;
            default: str= "Project Name";
        }
        return (
            <div>
                <select id="sort choice" value={str} onChange={this.change}>
                    <option value="1">Project Name</option>
                    <option value="2">Project Name Reverse</option>
                    <option value="1">Developer Name</option>
                    <option value="1">Developer Name Reverse</option>
                </select>
            </div>
        )
    }
}

export default SortBy
