import React, { Component } from "react";
import "./DisplayTeacher.css";
import Axios from "axios";

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            date: "",
            start: "",
            end: "",
            task: "",
        };
    }
    send = () => {
        // Axios.post("http://localhost:3001/insert",{
        //     date:date, names:name                       //backend(col names) : frontend
        // }).then((response)=>{
        //     console.log(response);
        // });
        // this.hide();
    };
    hide = () => {
        var element = document.getElementById("hiddenForm");
        element.classList.remove("visibleForm");
    };
    render() {
        return (
            <div id="hiddenForm" className="hiddenForm">
                <h1 className="m0">Add Details</h1>
                <p className="m0">Enter teacher's name</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ name: e.target.value });
                        }}
                        className="w m0"
                        type="text"
                        placeholder="Tony Stark"
                    />
                </div>
                <p className="m0">Date</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ date: e.target.value });
                        }}
                        className="w m0"
                        type="date"
                        placeholder="Month-Day"
                    />
                </div>
                <p className="m0">Start Time</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ start: e.target.value });
                        }}
                        className="w m0"
                        type="time"
                        placeholder="02:00"
                    />
                </div>
                <p className="m0">End Time</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ end: e.target.value });
                        }}
                        className="w m0"
                        type="time"
                        placeholder="03:00"
                    />
                </div>
                <p className="m0">Task</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ name: e.target.value });
                        }}
                        className="w m0"
                        type="text"
                        placeholder="Subject"
                    />
                </div>
                <div className="formButtons">
                <button className="formButton" onClick={() => this.send}>Add Schedule</button>
                <button className="formButton" onClick={() => this.hide()}>Close</button>
                </div>
            </div>
        );
    }
}

export default Form;
