import React,{useState} from 'react'
import Axios from "axios"



function DisplayFunction(props) {

    const [toggle,setToggle] = useState(false);


    let wholeData = [];
    const tempFunc =(useName)=>{
        return new Promise((resolve)=>{
            let profileData = {};
            Axios.get("http://localhost:3001/profile",{params:{name:i.name}}).then((response)=>{
                    profileData[useName] = [];
                    for(let i =0;i<response.data.length;i++){
                        profileData[useName].push(response.data[i]);
                    }
                    resolve(profileData)
            })
        })
    }
    const myfunct = async() => {
        await Axios.get("http://localhost:3001/search",{params: {name : props.str}}).then((response)=>{
            response.data.map(async (i)=>{
                let useName = i.name;
                let temp = await tempFunc(useName)
                wholeData.push(profileData);
            })
            setToggle(true);
        })
        console.log(wholeData);
        console.log(wholeData.length);
    }



    return (
        <div className="displayFunction">
            <hr />
            Display Function
            <button onClick={myfunct}>
                click me 
            </button>
            {toggle ? (
                    wholeData.map((val,index)=>{
                        return(
                            <h1>
                            {wholeData[index].name}
                            </h1>
                        )
                        
                    })) : (
                        <>
                        Empty
                        </>
                    )
            }

        </div>
    )
}

export default DisplayFunction
