import Day from "./Day";
import Week from "./Week";
import Month from "./Month";
import "./App.css";
import React, { Component } from "react";

class Topbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            view: "Day",
            ops: 0,
            str: "",
            slider: 0,
        };
    }
    // static getDerivedStateFromProps(props,state){
    //     return ({str: props.str});
    // }
    changeView = (e) => {
        this.setState({ view: e.target.value }, () => {
            console.log(this.state.view);
        });
    };
    changeOps = (e) => {
        this.setState({ ops: e.target.value });
    };
    render() {
        const curr = this.state;
        console.log(curr.str);
        console.log(this.props.str);
        let view = <Day str={this.props.str} />;
        let options = [];
        if (curr.view === "Day") {
            for (let i = 1; i <= 30; i++) options.push(i);
        } else if (curr.view === "Week") {
            view = <Week str={this.props.str} />;
            for (let i = 1; i <= 5; i++) options.push(i);
        } else if (curr.view === "Month") {
            view = <Month str={this.props.str} />;
            options = [1];
        }
        return this.props.enter ? (
            <React.Fragment>
                <div className="header2">
                    <button className="back" onClick={this.props.back}>
                        BACK
                    </button>
                    <input
                        onChange={(e) => {
                            this.state.slider(e.target.value);
                        }}
                        type="range"
                        id="vol"
                        name="vol"
                        min="0"
                        max="30"
                        step="1"
                    />
                    {this.state.slider}
                    <select
                        className="viewType"
                        onChange={(e) => {
                            this.changeOps(e);
                        }}
                    >
                        {options.map((val, index) => (
                            <option value={val} key={index}>
                                {val}
                            </option>
                        ))}
                    </select>
                    <select
                        className="viewType"
                        onChange={this.props.changeView}
                    >
                        <option type="1"> Day </option>
                        <option type="2"> Week </option>
                        <option type="3"> Month </option>
                    </select>
                </div>
                {view}
            </React.Fragment>
        ) : (
            <div className="header">
                <div className="headerFont">
                    <div className="oneLine">
                        <input
                            placeholder="🔎 Search Teacher"
                            className="input"
                            type="text"
                            onChange={(e) => this.props.textChange(e)}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Topbar;
