import React, { Component } from 'react'
import "./DisplayTeacher.css"
import Axios from 'axios';

class Form extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: "",
            date: "",
            start: "",
            end: "",
            task: ""
        }
    }
    send=()=>{
        // Axios.post("http://localhost:3001/insert",{
        //     date:date, names:name                       //backend(col names) : frontend
        // }).then((response)=>{
        //     console.log(response);
        // });
        this.hide();
    }
    hide=()=>{
        var element = document.getElementById("visibleForm");
        element.className= "hiddenForm";
    }
    render() {
        return (
        <form id="hiddenForm" className="hiddenForm">
            <h1 className="m0">Add Details</h1>
            <p className="m0">Enter teacher's name</p>
            <input onChange={(e)=>{ this.setState({name: e.target.value})}}
             className="w m0" type="text" placeholder="Tony Stark"/>
            <p className="m0">Date</p>
            <input onChange={(e)=>{ this.setState({name: e.target.value})}}
             className="w m0" type="text" placeholder="Tony Stark"/>
            <p className="m0">Start Time</p>
            <input onChange={(e)=>{ this.setState({name: e.target.value})}}
             className="w m0" type="text"/>
            <p className="m0">End Time</p>
            <input onChange={(e)=>{ this.setState({name: e.target.value})}}
             className="w m0" type="text"/>
            <p className="m0">Task</p>
            <input onChange={(e)=>{ this.setState({name: e.target.value})}}
             className="w m0" type="text" placeholder="Tony Stark"/>
            <button onClick={()=>this.send}>Add Schedule</button>
            <button onClick={()=>this.hide()}>Close</button>
        </form>
        )
    }
}

export default Form
