import Day from "./Day";
import Week from "./Week";
import Month from "./Month";
import "./App.css";
import React, { Component } from "react";

class Topbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            view: "Day",
            ops: 0,
            slider: 0,
        };
    }
    // static getDerivedStateFromProps(props,state){
    //     return ({str: props.str});
    // }
    changeView = (e) => {
        this.setState({ view: e.target.value }, () => {
            console.log(this.state.view);
        });
    };
    changeOps = (e) => {
        this.setState({ ops: e.target.value });
    };
    render() {
        const curr = this.state;
        let view = <Day str={this.props.str} />;
        if (curr.view === "Week") {
            view = <Week str={this.props.str} />;
        } else if (curr.view === "Month") {
            view = <Month str={this.props.str} />;
        }
        return this.props.enter ? (
            <React.Fragment>
                <div className="header2">
                    <button className="back" onClick={this.props.back}>
                        BACK
                    </button>
                    <input
                        onChange={(e) => {
                            this.setState({slider: e.target.value});
                        }}
                        type="range"
                        id="vol"
                        value
                        min= {0}
                        max= {30}
                        step="1"
                    />
                    {this.state.slider}
                    <select
                        className="viewType"
                        onChange={this.props.changeView}
                    >
                        <option type="1"> Day </option>
                        <option type="2"> Week </option>
                        <option type="3"> Month </option>
                    </select>
                </div>
                {view}
            </React.Fragment>
        ) : (
            <div className="header">
                <div className="headerFont">
                    <div className="oneLine">
                        <input
                            placeholder="🔎 Search Teacher"
                            className="input"
                            type="text"
                            onChange={(e) => this.props.textChange(e)}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Topbar;
