import React,{useState} from 'react'
import Axios from "axios"



function DisplayFunction(props) {

    const [toggle,setToggle] = useState(false);


    let wholeData = [];
    const myfunct = async() => {
        Axios.get("http://localhost:3001/search",{params: {name : props.str}}).then((response)=>{
            response.data.map((i)=>{
                let profileData = {};
                let useName = i.name;
                Axios.get("http://localhost:3001/profile",{params:{name:i.name}}).then((response)=>{
                    profileData[useName] = [];
                    for(let i =0;i<response.data.length;i++){
                        profileData[useName].push(response.data[i]);
                    }
                })
                awaitwholeData.push(profileData);
                console.log(wholeData);
                console.log(wholeData.length);
            })
        })
        console.log(wholeData);
        console.log(wholeData.length);
        setToggle(true);
    }



    return (
        <div className="displayFunction">
            <hr />
            Display Function
            <button onClick={myfunct}>
                click me 
            </button>
            {toggle ? (
                    wholeData.map((val,index)=>{
                        return(
                            <h1>
                            {wholeData[index].name}
                            </h1>
                        )
                        
                    })) : (
                        <>
                        Empty
                        </>
                    )
            }

        </div>
    )
}

export default DisplayFunction
