import React, { Component } from 'react';
import SearchTeacher from './SearchTeacher';
import SearchResult from './SearchResult';
import ViewType from './ViewType';
import './App.css';
import DisplayTeachers from './DisplayTeachers';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      teacher: "",
      matches: "-1",
      selected: false,
    }
  }
  searchForTeacher= name =>{
    this.setState({teacher : name},()=>console.log(this.state.teacher));
  }
  render(){
    let curr= this.state;
    console.log()
    let topbar= <SearchTeacher search={this.searchForTeacher}/>;
    if(curr.selected) topbar= 
    <React.Fragment>
      <button onClick= "find"> back </button>
      <ViewType/> 
    </React.Fragment>
    return(
      <div>
        <div className= "header">
          {topbar}
        </div>
        <div className= "body">
          <SearchResult matches={curr.matches}/>
          <DisplayTeachers str={curr.teacher}/>
        </div>
      </div>
    ) 
  }
}


export default App;
