import React, { Component } from 'react'
import Axios from 'axios'

class DisplayTeachers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: [],
            
        }
    }
    
    
    static async getDerivedStateFromProps(props,state){
        let name= props.str;
        let dbdata= [];
        function fn(e){
            return new Promise(resolve=>{
                Axios.get("http://localhost:3001/search",{params: {name : e}}).then((response)=>{
                    response.data.map(async (data,index)=>{
                        let profiledata= await tempFunc(data.name,e);
                        dbdata.push(profiledata);
                    });
                    resolve(dbdata);
                });
            })
        }
        const tempFunc =(useName,e)=>{
            return new Promise((resolve)=>{
                let profiledata=[];
                    profiledata.push(useName);
                    Axios.get("http://localhost:3001/profile",{params: {name : useName}}).then((res)=>{
                        const data= res.data;      //JSON
                        for(let i=0; i<data.length; i++){
                            const date= data[i].date.substring(5,10);
                            profiledata.push(date);
                            profiledata.push(data[i].starttime);
                            profiledata.push(data[i].endtime);
                            profiledata.push(data[i].task);
                        }
                        resolve(profiledata);
                    });
            })
        }
        let abc = await fn(name);
        console.log(abc);
        this.state
        return ({show : abc});
    }
    render() {
        let curr= this.state.show;
        console.log(curr);
