import React, { Component } from 'react'
import Axios from 'axios'

class DisplayTeachers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: [],
        }
    }
    static getDerivedStateFromProps(props,state){
        let name= props.str;
        let names= [];
        let dbdata= [];
        Axios.get("http://localhost:3001/search",{params: { name : name}}).then((response)=>{
            return response.data.map((data,index)=>{
                let name= data.name;
                names.push(name);
                Axios.get("http://localhost:3001/profile",{params: { name : name }}).then((response)=>{
                    dbdata.push(response.data);
                });
            });
        });
        return ({show : dbdata});
    }
    render() {
