import React, { Component } from 'react';
import SearchTeacher from './SearchTeacher';
import SearchResult from './SearchResult';
import ViewType from './ViewType';
import Axios from 'axios'
import './App.css';
import DisplayTeachers from './DisplayTeachers';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      matches: "-1",
      show: [],
      display: "",
    }
    this.abcd= this.abcd.bind(this);
  }
  async abcd(){
      let name= this.state.display;
      let dbdata= [];
      function getName(e){
          return new Promise(async (resolve)=>{
              await Axios.get("http://localhost:3001/search",{params: {name : e}})
              .then(async (response)=>{
                  for(let i=0; i<response.data.length; i++){
                      let profiledata= await getData(response.data[i].name,e);
                      dbdata.push(profiledata);
                  }
                  resolve(dbdata);
              });
          })
      }
      const getData =(useName,e)=>{
          return new Promise(async (resolve)=>{
              let profiledata=[];
              profiledata.push(useName);
              await Axios.get("http://localhost:3001/profile",{params: {name : useName}})
              .then((res)=>{
                  const data= res.data;      //JSON
                  for(let i=0; i<data.length; i++){
                      const date= data[i].date.substring(5,10);
                      profiledata.push(date);
                      profiledata.push(data[i].starttime.substring(0,5));
                      profiledata.push(data[i].endtime.substring(0,5));
                      profiledata.push(data[i].task);
                  }
                  resolve(profiledata);
              });
          })
      }
      let abc = await getName(name);
      this.setState({show : abc});
  }
  render(){
    let curr= this.state.show;
      let names= [];
      for(let i=0; i<curr.length; i++) names.push(curr[i].shift());
      let i= 0;
      return (
      <div>
        <div className= "header">
          <div className= "headerFont">
            <div className= "oneLine">
            <input placeholder="  Search Teacher" className= "input"
            type= "text" onChange={this.textChange}/>
          </div>
        </div>
      </div>
      <div className="teacher__parent">
      {
        curr.map((data,index)=>(
        <div  className= "teacher__container" key={index}>
          <div className= "teacher__name" key={index}>
          <h2>{names[i++]}</h2>
          </div>
          {
            data.map((val,ind)=>(
              <div className= "teacher__data" key={ind}>{val}</div>
            ))
          } 
          <div className="needPading"></div>
          </div>
        ))
      }
      </div>
      <div className="bottom__button">
      <form id="hidden__form" className="hidden__form">
        <h1 className="m0">Add Details</h1>
        <p className="m0">Enter teacher's name</p>
        <input className="w" type="text"/>
        <p className="m0">Date</p>
        <input className="w" type="text"/>
        <p className="m0">Start Time</p>
        <input className="w" type="text"/>
        <p className="m0">End Time</p>
        <input className="w" type="text"/>
        <p className="m0">Task</p>
        <input className="w" type="text"/>
      </form>
      <button onClick={()=>{
        var element = document.getElementById("hidden__form");
        element.classList.add("visible__form");
        element.classList.remove("hidden__form");
      }} className="bottom__button__inside">
      <img src="https://www.gstatic.com/images/icons/material/colored_icons/1x/create_32dp.png" /> CREATE </button>
      </div>
      </div>
      ) 
  }
  componentDidMount(){
      this.abcd();
  }
}


export default App;
