import React, { Component } from "react";
import "./DisplayTeacher.css";
import Axios from "axios";

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            date: "",
            start: "",
            end: "",
            task: "",
        };
    }
    send = () => {
        let curr= this.state;
        Axios.post("http://localhost:3001/insert",
            {params:{name: curr.name, date:curr.date, starttime: curr.start,
                 endtime: curr.end, }}
        ).then((response)=>{
            console.log(response);
        });
        this.hide();
    };
    hide = () => {
        var element = document.getElementById("Form");
        element.classList.remove("visibleForm");
        element.classList.add("hiddenForm");
    };
    render() {
        return (
            <div id="Form" className="hiddenForm">
                <h1 className="m0">Add Details</h1>
                <p className="m0">Enter teacher's name</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ name: e.target.value });
                        }}
                        className="w m0"
                        type="text"
                        placeholder="Tony Stark"
                    />
                </div>
                <p className="m0">Date</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ date: e.target.value });
                        }}
                        className="w m0"
                        type="date"
                        placeholder="Month-Day"
                    />
                </div>
                <p className="m0">Start Time</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ start: e.target.value });
                        }}
                        className="w m0"
                        type="time"
                        placeholder="02:00"
                    />
                </div>
                <p className="m0">End Time</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ end: e.target.value });
                        }}
                        className="w m0"
                        type="time"
                        placeholder="03:00"
                    />
                </div>
                <p className="m0">Task</p>
                <div>
                    <input
                        onChange={(e) => {
                            this.setState({ name: e.target.value });
                        }}
                        className="w m0"
                        type="text"
                        placeholder="Subject"
                    />
                </div>
                <div className="formButtons">
                    <button className="formButton" onClick={() => this.send()}>
                        Add Schedule
                    </button>
                    <button
                        className="formButton"
                        onClick={()=> this.hide()}
                    >
                        Close
                    </button>
                </div>
            </div>
        );
    }
}

export default Form;
