import React, { Component } from 'react'

class SortBy extends Component {
    constructor(props) {
        super(props)
        this.state = {
            display: props.sortDisplay
        }
    }
    change= e =>{
        let val= e.target.value;
        this.setState({display : val},()=>{this.props.sortSelect(val)});
    }
    render() {
        let choice= this.state.display;
        console.log(choice);
        return (
            <div>
                <select id="sort choice" value={choice} onChange={this.change}>
                    <option value="1">Project Name</option>
                    <option value="2">Project Name Reverse</option>
                    <option value="3">Developer Name</option>
                    <option value="4">Developer Name Reverse</option>
                </select>
            </div>
        )
    }
}

export default SortBy
