import React, { Component } from "react";
import Axios from "axios";
import "./App.css";
import Form from "./Form";
import "./DisplayTeacher.css";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            show: [],
            display: "",
        };
        this.abcd = this.abcd.bind(this);
    }
    textChange = (e) => {
        this.setState({ display: e.target.value }, () => {
            this.abcd();
        });
    };
    async abcd() {
        let name = this.state.display;
        let dbdata = [];
        function getName(e) {
            return new Promise(async (resolve) => {
                await Axios.get("http://localhost:3001/search", {
                    params: { name: e },
                }).then(async (response) => {
                    for (let i = 0; i < response.data.length; i++) {
                        let profiledata = await getData(
                            response.data[i].name,
                            e
                        );
                        dbdata.push(profiledata);
                    }
                    resolve(dbdata);
                });
            });
        }
        const getData = (useName, e) => {
            return new Promise(async (resolve) => {
                let profiledata = [];
                profiledata.push(useName);
                await Axios.get("http://localhost:3001/profile", {
                    params: { name: useName },
                }).then((res) => {
                    const data = res.data; //JSON
                    for (let i = 0; i < data.length; i++) {
                        const date = data[i].date.substring(5, 10);
                        profiledata.push(date);
                        profiledata.push(data[i].starttime.substring(0, 5));
                        profiledata.push(data[i].endtime.substring(0, 5));
                        profiledata.push(data[i].task);
                    }
                    resolve(profiledata);
                });
            });
        };
        let abc = await getName(name);
        this.setState({ show: abc });
    }
    render() {
        let curr = this.state;
        let names = [];
        for (let i = 0; i < curr.show.length; i++)
            names.push(curr.show[i].shift());
        let i = 0;
        return (
            <div>
                <div className="header">
                    <div className="headerFont">
                        <div className="oneLine">
                            <input
                                placeholder="   🔎 Search Teacher"
                                className="input"
                                type="text"
                                onChange={this.textChange}
                            />
                        </div>
                    </div>
                </div>
                <div className="teacherParent">
                    {curr.show.map((data, index) => (
                        <div className="teacherContainer" key={index}>
                            <div className="teacherName" key={index}>
                                <h2>{names[i++]}</h2>
                            </div>
                            {data.map((val, ind) => (
                                <div className="teacherData" key={ind}>
                                    {val}
                                </div>
                            ))}
                            <div className="needPading"></div>
                        </div>
                    ))}
                </div>
                <div className="bottomButton">
                    <Form str={curr.display} />
                    <button
                        onClick={() => {
                            var element = document.getElementById("Form");
                            element.classList.add("visibleForm");
                            element.classList.remove("hiddenForm");
                        }}
                        className="bottomButtonInside"
                    >
                        <img src="https://www.gstatic.com/images/icons/material/colored_icons/1x/create_32dp.png" />{" "}
                        CREATE{" "}
                    </button>
                </div>
            </div>
        );
    }
    componentDidMount() {
        this.abcd();
    }
}

export default App;
