import React, { Component } from "react";
import Axios from "axios";
import "./App.css";
import Form from "./Form";
import Topbar from "./Topbar";
import "./DisplayTeacher.css";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            show: [],
            display: "",
            enter: false,
            chosen: "",
        };
        this.getData = this.getData.bind(this);
    }
    textChange = (e) => {
        this.setState({ display: e.target.value }, () => {
            this.getData();
        });
    };
    enter = (e) => {
        this.setState({ enter: true, chosen: e.target.id });
    };
    back = (e) => {
        window.location.reload();
    };
    makeVisible = (e) => {
        var element = document.getElementById("Form");
        element.classList.add("visibleForm");
        element.classList.remove("hiddenForm");
    };
    async getData() {
        let name = this.state.display;
        let dbdata = [];
        function getName(str) {
            return new Promise(async (resolve) => {
                await Axios.get("http://localhost:3001/search", {
                    params: { name: str },
                }).then(async (response) => {
                    for (let i = 0; i < response.data.length; i++) {
                        let profiledata = await getData(
                            response.data[i].name,
                            str
                        );
                        dbdata.push(profiledata);
                    }
                    resolve(dbdata);
                });
            });
        }
        const getData = (useName, str) => {
            return new Promise(async (resolve) => {
                let profiledata = [];
                profiledata.push(useName);
                await Axios.get("http://localhost:3001/profile", {
                    params: { name: useName },
                }).then((res) => {
                    const data = res.data; //JSON
                    for (let i = 0; i < data.length; i++) {
                        console.log(data[i].date);
                        const date = data[i].date.substring(5, 10);
                        profiledata.push(date);
                        console.log(date);
                        profiledata.push(data[i].starttime.substring(0, 5)+"-"+
                        data[i].endtime.substring(0, 5));
                        profiledata.push(data[i].task);
                    }
                    resolve(profiledata);
                });
            });
        };
        let data = await getName(name);
        console.log(data);
        this.setState({ show: data });
    }
    render() {
        let curr = this.state;
        let names = [];
        for (let i = 0; i < curr.show.length; i++)
            names.push(curr.show[i].shift());
        let k = 0;
        if (curr.enter) {
            return (
                <div>
                    <Topbar
                        textChange={this.textChange}
                        back={() => this.back()}
                        enter={curr.enter}
                        str={curr.chosen}
                    />
                </div>
            );
        }
        return (
            <div>
                <Topbar textChange={this.textChange} enter={curr.enter} />
                <div className="teacherParent">
                    {curr.show.map((data, index) => (
                        <div
                            className="teacherContainer"
                            key={index}
                            onClick={(e) => this.enter(e)}
                        >
                            <div className="teacherName" key={index}>
                                <h2 id={names[k]}>{names[k++]}</h2>
                            </div>
                            {
                                data.map((val,index)=>(
                                <div className="teacherData" key={index} id={names[k-1]}>
                                {val}
                                <br />
                                </div>
                                ))
                            }
                            <div className="needPading"></div>
                        </div>
                    ))}
                </div>
                <div className="create">
                    <Form str={curr.display} />
                    <button
                        className="createInside"
                        onClick={(e) => this.makeVisible(e)}
                    >
                        <img
                            alt="add"
                            src="https://www.gstatic.com/images/icons/material/colored_icons/1x/create_32dp.png"
                        />
                        <p>CREATE</p>
                    </button>
                </div>
            </div>
        );
    }
    componentDidMount() {
        this.getData();
    }
}

export default App;
