import React, { Component } from 'react'
import Axios from 'axios'
import './App.css';

class DisplayTeachers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: [],
            display: "",
        }
        this.abcd= this.abcd.bind(this);
    }
    
    textChange= e =>{
        this.setState({display: e.target.value});
    }
    componentDidMount(){
        this.abcd();
    }
    async abcd(){
        let name= this.state.display;
        let dbdata= [];
        function getName(e){
            return new Promise(async (resolve)=>{
                await Axios.get("http://localhost:3001/search",{params: {name : e}})
                .then(async (response)=>{
                    for(let i=0; i<response.data.length; i++){
                        let profiledata= await getData(response.data[i].name,e);
                        dbdata.push(profiledata);
                    }
                    resolve(dbdata);
                });
            })
        }
        const getData =(useName,e)=>{
            return new Promise(async (resolve)=>{
                let profiledata=[];
                profiledata.push(useName);
                await Axios.get("http://localhost:3001/profile",{params: {name : useName}})
                .then((res)=>{
                    const data= res.data;      //JSON
                    for(let i=0; i<data.length; i++){
                        const date= data[i].date.substring(5,10);
                        profiledata.push(date);
                        profiledata.push(data[i].starttime);
                        profiledata.push(data[i].endtime);
                        profiledata.push(data[i].task);
                    }
                    resolve(profiledata);
                });
            })
        }
        let abc = await getName(name);
        this.setState({show : abc});
    }
    render() {
        let curr= this.state.show;
        console.log(curr);
        return (
        <div>
            <div className= "header">
            <div className= "headerFont">
                <label>Search Teacher</label>
                <div className= "oneLine">
                    <input className= "input" type= "text" onChange={this.textChange} value={curr.display}/>
                    <button className= "find" onClick={this.abcd}>
                        <span className= "span">Find</span>
                        <span className= "span">Find</span>
                    </button>
                </div>
            </div>
            </div>
            {
                curr.map((data,index)=>(
                <div className= "title">
                    <div key={index}>
                    {
                        data.map((val,ind)=>(
                            <div key={ind}>{val}</div>
                        ))
                    } 
                    </div>
                </div>
                ))
            }
            
        </div>
        )
    }
}

export default DisplayTeachers
