import React, { Component } from 'react';
import SearchProject from './SearchProject';
import SearchName from './SearchName';
import SortBy from './SortBy';
import SearchResult from './SearchResult';
import Grid from './Grid';
import './App.css';
import Display from './Display';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      projectSearch: "",
      nameSearch: "",
      sortedBy: "0",
      matches: "-1",
    }
  }
  static getDerivedStateFromProps(props,state){
  }
  searchForProjects= str =>{
    this.setState({projectSearch : str},()=>console.log(this.state.projectSearch));
  }
  searchForNames= str =>{
    this.setState({nameSearch : str},()=>console.log(this.state.nameSearch));
  }
  sortSelection= str =>{
    this.setState({sortedBy : str},()=>console.log(this.state.sortedBy));
  }
  render(){
    let curr= this.state;
    let arr= curr.array;
    if(curr.projectSearch.length > 0){
      arr= {project: curr.projectSearch};              //pass string instead of array
    }else if(curr.nameSearch.length > 0){
      arr= {name: curr.nameSearch};                 //make use of js
    }else if(curr.sortedBy === "1") arr= curr.arr1;
    else if(curr.sortedBy === "2") arr= curr.arr2;
    else if(curr.sortedBy === "3") arr= curr.arr3;
    else if(curr.sortedBy === "4") arr= curr.arr4;
    return(
      <div>
        <div className= "header">
          <div className= "swipe"/>
          <SearchProject search={this.searchForProjects}/>
          <SearchName search={this.searchForNames}/>
          <SortBy sortSelect={this.sortSelection} sortDisplay={curr.sortedBy}/>
        </div>
        <div className= "body">
          <SearchResult matches={curr.matches}/>
          <Grid array={arr}/>
          <Display></Display>
        </div>
      </div>
    ) 
  }
}


export default App;
