import './App.css';
import DisplayTeachers from './DisplayTeachers';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      matches: "-1",
      show: [],
      display: "",
    }
    this.abcd= this.abcd.bind(this);
  }
  async abcd(){
