import React, { Component } from 'react';
import SearchProject from './SearchProject';
import SearchName from './SearchName';
import SortBy from './SortBy';
import SearchResult from './SearchResult';
import Grid from './Grid';
import './App.css';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
       projectSerach: "",
       nameSearch: "",
       sortedBy: "1",
       matches: "1",
       profiles: "20",
    }
  }
  searchForProjects= str =>{
    this.setState({projectSerach : str},()=>console.log(this.state.projectSerach));
  }
  searchForNames= str =>{
    this.setState({nameSearch : str},()=>console.log(this.state.nameSearch));
  }
  sortSelection= str =>{
    this.setState({sortedBy : str},()=>console.log(this.state.sortedBy));
  }
  render(){
    let curr= this.state;
    return(
      <div>
        <div className= "header">
          <SearchProject search={this.searchForProjects}/>
          <SearchName search={this.searchForNames}/>
          <SortBy sortSelect={this.sortSelection} sortDisplay={curr.sortedBy}/>
        </div>
        <div className= "body">
          <SearchResult matches={curr.matches}/>
          <Grid profiles={curr.profiles}/>
        </div>
      </div>
     ) 
  }
}


export default App;
