import React, { Component } from 'react'
import Axios from 'axios'

class DisplayTeachers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: [],
            str: "",
        }
    }
    static getDerivedStateFromProps(props,state){
        return ({str : props.str});
    }
    async abcd(str){
        console.log(str);
        let name= str;
        let dbdata= [];
        function fn(e){
            return new Promise(async (resolve)=>{
                await Axios.get("http://localhost:3001/search",{params: {name : e}})
                .then(async (response)=>{
                    for(let i=0; i<response.data.length; i++){
                        let profiledata= await tempFunc(response.data[i].name,e);
                        dbdata.push(profiledata);
                    }
                    resolve(dbdata);
                });
            })
        }
        const tempFunc =(useName,e)=>{
            return new Promise(async (resolve)=>{
                let profiledata=[];
                    profiledata.push(useName);
                    await Axios.get("http://localhost:3001/profile",{params: {name : useName}}).then((res)=>{
                        const data= res.data;      //JSON
                        for(let i=0; i<data.length; i++){
                            const date= data[i].date.substring(5,10);
                            profiledata.push(date);
                            profiledata.push(data[i].starttime);
                            profiledata.push(data[i].endtime);
                            profiledata.push(data[i].task);
                        }
                        resolve(profiledata);
                    });
            })
        }
        let abc = await fn(name);
        this.setState({show : abc});
    }
    render() {
        let curr= this.state.show;
        abcd(props.str);
        console.log(curr);
        console.log("Hola");
        return (
            <div>
            {
                this.state.show.map((data,index)=>(
                    <div>
                    {
                        data.map((val,ind)=>(
                            <div key={ind}>{val}</div>
                        ))
                    } 
                    </div>
                ))
            }
            </div>
        )
    }
}

export default DisplayTeachers
