import React, { Component } from 'react';
import SearchTeacher from './SearchTeacher';
import SearchResult from './SearchResult';
import Grid from './Grid';
import ViewType from './ViewType';
import './App.css';
import Display from './Display';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      teacher: "",
      matches: "-1",
      selected: false,
    }
  }
  searchForTeacher= name =>{
    this.setState({teacher : name},()=>console.log(this.state.teacher));
  }
  render(){
    let curr= this.state;
    return(
      <div>
        <div className= "header">
            <SearchTeacher search={this.searchForTeacher}/>
            <button onClick= "find"> back </button>
            <ViewType/>
        </div>
        <div className= "body">
          <SearchResult matches={curr.matches}/>
          <Grid/>
          <Display></Display>
        </div>
      </div>
    ) 
  }
}


export default App;
