import React, { Component } from 'react';
import SearchProject from './SearchProject';
import SearchName from './SearchName';
import SortBy from './SortBy';
import SearchResult from './SearchResult';
import Grid from './Grid';
import './App.css';

class App extends Component{
  constructor(props) {
    super(props)
    this.state = {
      projectSearch: "",
      nameSearch: "",
      sortedBy: "0",
      matches: "-1",
      array: [],
      arr1: [],
      arr2: [],
      arr3: [],
      arr4: [],
    }
  }
  static getDerivedStateFromProps(props,state){
    if(state.array.length > 0) return({});
    let original= [ {img:" ", title:"a", dev:" ", github:" "},
      {img:" ", title:"b", dev:" ", github:" "},
      {img:" ", title:"c", dev:" ", github:" "},
      {img:" ", title:"d", dev:" ", github:" "},
      {img:" ", title:"e", dev:" ", github:" "},
      {img:" ", title:"f", dev:" ", github:" "},
      {img:" ", title:"g", dev:" ", github:" "},
      {img:" ", title:"h", dev:" ", github:" "}
    ];
    let arr1= [...original];  //copy with spread operator
    let arr3= [...original]; 
    arr1.sort((a,b) => a.title-b.title);
    arr3.sort((a,b) => a.dev-b.dev);
    let arr2= [...arr1];
    let arr4= [...arr3];
    arr2.reverse();
    arr4.reverse();
    return ({array : original});
  }
  searchForProjects= str =>{
    this.setState({projectSearch : str},()=>console.log(this.state.projectSerach));
  }
  searchForNames= str =>{
    this.setState({nameSearch : str},()=>console.log(this.state.nameSearch));
  }
  sortSelection= str =>{
    this.setState({sortedBy : str},()=>console.log(this.state.sortedBy));
  }
  render(){
    let curr= this.state;
    return(
      <div>
        <div className= "header">
          <SearchProject search={this.searchForProjects}/>
          <SearchName search={this.searchForNames}/>
          <SortBy sortSelect={this.sortSelection} sortDisplay={curr.sortedBy}/>
        </div>
        <div className= "body">
          <SearchResult matches={curr.matches}/>
          <Grid array={curr.array}/>
        </div>
      </div>
     ) 
  }
}


export default App;
