import React, { Component } from 'react'
import Axios from 'axios'

class DisplayTeachers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: [],
        }
    }
    static getDerivedStateFromProps(props,state){
        let name= props.str;
        let dbdata= [];
        Axios.get("http://localhost:3001/search",{params: {name : name}}).then((response)=>{
            response.data.map((data,index)=>{
                let profiledata=[];
                let name= data.name;
                let datetask= {};
                profiledata.push(name);
                Axios.get("http://localhost:3001/profile",{params: {name : name}}).then((response)=>{
                    const data= response.data;      //JSON
                    for(let i=0; i<data.length; i++){
                        const date= data[i].date.substring(5,10);
                        let tasks= [];
                        if(datetask.date!== undefined)
                        console.log(date);
                        tasks.push(data.starttime);
                        tasks.push(data.endtime);
                        tasks.push(data.task);
                        datetask.date= tasks;        //object having date mapping to tasks array
                        profiledata.push(datetask);
                    }
                });
                dbdata.push(profiledata);
            });
        });
        return ({show : dbdata});
    }
    render() {
        const curr= this.state.show;
